import './Content.css';

interface ContextProp {

}

export const Content = (props:any) => {



    const LoadingScreen = () => {
        return (
    <div className="loading-screen">
      <div className="loading-spinner">
        <div className="spinner"></div>
      </div>
    </div>
        );

    }

    const dataScreen = () => {
        return (
            <h2>{props.data.main.temp} ˚C</h2>
        );

    }


    return (
        <div id="content">
          {props.loading ? (
            <div className="loading-spinner">
              <div className="spinner"></div>
              <p>Loading...</p>
            </div>
          ) : (
            <div className="data-screen">
              <h2>{props.data.main.temp} ˚C</h2>
            </div>
          )}
        </div>
      );
}
