import React, {useState,useEffect} from 'react';
import './Content.css';

interface ContextProp {

}

export const Content = (props:any) => {



    const LoadingScreen = () => {
        return (
      <div className="loading-spinner">
        <div className="spinner"></div>
      </div>
        );

    }

    const riseSet = (time:number) => {
        const timestamp = time;
        const milliseconds = timestamp * 1000; 
        const date = new Date(milliseconds);
        const result = date.toLocaleTimeString('en-US', {
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit',
            hour12: true,
            });
        return result;
    }

    const dataScreen = () => {


    console.log('Sunrise time:', riseSet(props.data.sys.sunrise));
    console.log('Sunset time:', riseSet(props.data.sys.sunset));
        return (
            <>
                <h2>{props.data.name}</h2>
                <h3>{props.data.main.temp} ˚C</h3>
                <h4>{props.data.main.feels_like} ˚C</h4>
                <p>{props.data.main.temp_min} ˚C</p>
                <p>{props.data.main.temp_max} ˚C</p>
            </>
        );

    }


    return (
        <div id="content">
            <div>
                {props.loading ? LoadingScreen() : dataScreen()}    
            </div>
        </div>
    )
}
