import React, {useState} from "react";
import './Main.css'

const Main = () => {

    const mainContainer = document.getElementsByClassName('main');

    const updateBoxShadow = () => {
        const now = new Date();
        const hour = now.getHours();

        // Calculate darkness factor based on the hour
        const darkness = Math.abs(12 - hour) / 12;

        // Calculate box shadow properties based on darkness
        const boxShadow = `0px 0px 20px rgba(0, 0, 0, ${darkness})`;

        // Apply the calculated box shadow to the container
        mainContainer.style.boxShadow = boxShadow;

        // Update the class to 'day' or 'night' based on darkness factor
        if (darkness > 0.5) {
            mainContainer.classList.add('night');
            mainContainer.classList.remove('day');
        } else {
            mainContainer.classList.add('day');
            mainContainer.classList.remove('night');
        }
    }

    return (
        <div className="main">

        </div>
    )
}

export default Main;
