import React, {useState,useEffect} from "react";
import './Main.css';
import { Content } from "./component/Content";
import { FiSearch, FiMapPin } from "react-icons/fi";
import PlacesAutocomplete, {
    geocodeByAddress,
    getLatLng,
  } from 'react-places-autocomplete';
import axios from "axios";




enum TimeOfDay {
    Morning = 'morning',
    Noon = 'noon',
    Sunset = 'sunset',
    Night = 'night'
  }



const Main = () => {

    const [timeOfDay, setTimeOfDay] = useState<TimeOfDay>(TimeOfDay.Night);
    const [currLat, setLat] = useState('');
    const [currLong, setLong] = useState('');
    const [data, setData] = useState('');
    const [isLoading, setIsLoading] = useState(true);
    const [selectedAddress, setSelectedAddress] = useState('');




    const getWeather = () => {
        console.log("call")
        const url = `https://api.openweathermap.org/data/2.5/weather`;

        const options  = {
            params: {
                lat:  currLat,
                lon:  currLong,
                appid: '668bbc3aca97dfcdee5e638e17bcb1e8',
                units: 'metric'
            }
        };

        axios.get(url,options).then((response) => {
            console.log(response.data);
            setData(response.data);
            setIsLoading(false);
        } )
        .catch((error) => {
            console.log(error);
        })

    }

    const success = (pos:any) => {
      const crd = pos.coords;
      setLat(crd.latitude);
      setLong(crd.longitude);


    }



    useEffect(() =>{
        const now = new Date();
        const hour = now.getHours();

        if (hour >= 6 && hour < 10) {
          setTimeOfDay(TimeOfDay.Morning);
        } else if (hour >= 10 && hour < 18) {
          setTimeOfDay(TimeOfDay.Noon);
        } else if (hour >= 18 && hour < 20) {
          setTimeOfDay(TimeOfDay.Sunset);
        } else {
          setTimeOfDay(TimeOfDay.Night);
        }


        navigator.geolocation.getCurrentPosition(success);


      }, []);


      useEffect(() => {
        getWeather();
        const intervalId = setInterval(getWeather, 30000);
        return () => clearInterval(intervalId);

      },[currLat,currLong]);


      const handleAddressSelect = async (address: string) => {
        try {
          const results = await geocodeByAddress(address);
          const latLng = await getLatLng(results[0]);
          setSelectedAddress(address);
          setLat(latLng.lat.toString());
          setLong(latLng.lng.toString());
        } catch (error) {
          console.error('Error selecting address:', error);
        }
      };







    return (
        <div id="main" className={`${timeOfDay}`}>
            <h1>Weather</h1>
          <Content data={data} loading={isLoading}/>
            <div id="menu">
              <div>
                <FiMapPin className="icon" onClick={getWeather}/>
              </div>
              <div>
              <PlacesAutocomplete
            value={selectedAddress}
            onChange={setSelectedAddress}
            onSelect={handleAddressSelect}
          >
            {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
              <div>
                <input
                  {...getInputProps({
                    placeholder: 'Search Places...',
                  })}
                />
                  <div>
                    {loading ? <div>Loading...</div> : null}
                    {suggestions.map((suggestion, index) => {
                      const { placeId, description } = suggestion;
                      return (
                        <div
                          {...getSuggestionItemProps(suggestion, {
                            style: {
                              backgroundColor: index % 2 === 0 ? '#f9f9f9' : '#ffffff',
                              float: 'left', // Add this line to make the description float
                              width: '100%', // Add this line to ensure proper width
                            },
                          })}
                        >
                          {description}
                        </div>
                      );
                    })}
                  </div>
              </div>
            )}
          </PlacesAutocomplete>
              </div>
            </div>
        </div>
    )
}

export default Main;
