import React, {useState,useEffect} from 'react';
import './Content.css';

interface ContextProp {

}

export const Content = (props:any) => {

    const [showContent, setShowContent] = useState(false);

  useEffect(() => {
    // When the loading state changes, update the content visibility
    setShowContent(!props.loading);
  }, [props.loading]);




    const LoadingScreen = () => {
        return (
      <div className="loading-spinner">
        <div className="spinner"></div>
      </div>
        );

    }

    const dataScreen = () => {
        return (
            <h2>{props.data.main.temp} ˚C</h2>
        );

    }


    return (
        <div id="content">
            <div className={`fade-transition ${showContent ? 'fade-entered' : ''}`}>
        {props.loading ? (
          <div className="loading-spinner">
            <div className="spinner"></div>
          </div>
        ) : (
          <h2>{props.data.main.temp} ˚C</h2>
        )}
      </div>
        </div>
    )
}
